INSERT INTO email_templates (name, category, html_content) VALUES

('Flash Sale Promo', 'Promotion', '<!DOCTYPE html><html><head><meta charset="utf-8"><meta name="viewport" content="width=device-width,initial-scale=1"></head><body style="margin:0;padding:0;background:#000;font-family:Arial,sans-serif"><div style="max-width:600px;margin:0 auto;background:linear-gradient(135deg,#ff6b6b 0%,#ee5a24 50%,#ff9ff3 100%)"><div style="padding:60px 40px;text-align:center"><div style="background:rgba(255,255,255,0.1);border:2px solid rgba(255,255,255,0.3);border-radius:50%;width:100px;height:100px;margin:0 auto 24px;display:flex;align-items:center;justify-content:center"><span style="color:#fff;font-size:40px">⚡</span></div><h1 style="color:#fff;font-size:36px;margin:0 0 16px 0;font-weight:bold;text-shadow:0 2px 4px rgba(0,0,0,0.3)">FLASH SALE</h1><p style="color:#fff;font-size:20px;margin:0 0 32px 0;opacity:0.9">Limited Time Only - Don''t Miss Out!</p><div style="background:rgba(255,255,255,0.15);border-radius:20px;padding:40px;margin:32px 0;backdrop-filter:blur(10px)"><div style="color:#fff;font-size:72px;font-weight:bold;margin:0 0 8px 0;text-shadow:0 2px 4px rgba(0,0,0,0.3)">50%</div><div style="color:#fff;font-size:24px;margin:0 0 16px 0;font-weight:600">OFF EVERYTHING</div><p style="color:#fff;font-size:16px;margin:0;opacity:0.9">Use code: <strong style="background:rgba(255,255,255,0.2);padding:4px 8px;border-radius:4px">FLASH50</strong></p></div><div style="margin:40px 0"><a href="#" style="background:#fff;color:#ee5a24;text-decoration:none;padding:20px 40px;border-radius:50px;font-size:18px;font-weight:bold;display:inline-block;box-shadow:0 4px 15px rgba(0,0,0,0.2);transition:transform 0.2s">Shop Now</a></div><div style="background:rgba(255,255,255,0.1);border-radius:12px;padding:20px;margin:32px 0"><p style="color:#fff;font-size:16px;margin:0 0 8px 0;font-weight:600">⏰ Hurry! Sale ends in:</p><div style="display:flex;justify-content:center;gap:16px"><div style="text-align:center"><div style="color:#fff;font-size:24px;font-weight:bold">23</div><div style="color:#fff;font-size:12px;opacity:0.8">HOURS</div></div><div style="text-align:center"><div style="color:#fff;font-size:24px;font-weight:bold">45</div><div style="color:#fff;font-size:12px;opacity:0.8">MINUTES</div></div><div style="text-align:center"><div style="color:#fff;font-size:24px;font-weight:bold">12</div><div style="color:#fff;font-size:12px;opacity:0.8">SECONDS</div></div></div></div></div><div style="background:rgba(0,0,0,0.3);padding:24px;text-align:center"><p style="color:#fff;font-size:12px;margin:0;opacity:0.7">*Offer valid until midnight. Terms and conditions apply.</p></div></div></body></html>'),

('Elegant Discount', 'Promotion', '<!DOCTYPE html><html><head><meta charset="utf-8"><meta name="viewport" content="width=device-width,initial-scale=1"></head><body style="margin:0;padding:0;background:#f7f8fc;font-family:Georgia,serif"><div style="max-width:600px;margin:0 auto;background:#fff;box-shadow:0 10px 30px rgba(0,0,0,0.1)"><div style="background:linear-gradient(135deg,#667eea 0%,#764ba2 100%);padding:48px 40px;text-align:center;position:relative"><div style="position:absolute;top:20px;right:20px;background:rgba(255,255,255,0.2);color:#fff;padding:8px 16px;border-radius:20px;font-size:12px;font-weight:600">LIMITED TIME</div><h1 style="color:#fff;font-size:32px;margin:0 0 16px 0;font-weight:normal">Exclusive Offer</h1><p style="color:#e0e7ff;font-size:18px;margin:0">For Our Valued Members</p></div><div style="padding:48px 40px"><div style="text-align:center;margin-bottom:40px"><h2 style="color:#1f2937;font-size:24px;margin:0 0 16px 0">Dear {{name}},</h2><p style="color:#4b5563;font-size:16px;line-height:1.6;margin:0">We''re excited to offer you an exclusive discount on our premium services. This special offer is our way of saying thank you for being part of our community.</p></div><div style="border:2px dashed #d1d5db;border-radius:12px;padding:32px;text-align:center;margin:32px 0;background:#fafbfc"><div style="color:#7c3aed;font-size:48px;font-weight:bold;margin:0 0 8px 0">25% OFF</div><p style="color:#1f2937;font-size:18px;margin:0 0 16px 0;font-weight:600">Premium Membership</p><div style="background:#7c3aed;color:#fff;display:inline-block;padding:8px 16px;border-radius:6px;font-size:14px;font-weight:600;letter-spacing:1px">CODE: PREMIUM25</div></div><div style="background:#f0f9ff;border-left:4px solid #0ea5e9;padding:24px;margin:32px 0"><h3 style="color:#0c4a6e;font-size:18px;margin:0 0 12px 0">What''s Included:</h3><ul style="color:#0c4a6e;font-size:14px;line-height:1.8;margin:0;padding-left:20px"><li>Access to all premium features</li><li>Priority customer support</li><li>Advanced analytics and reporting</li><li>Custom integrations</li><li>30-day money-back guarantee</li></ul></div><div style="text-align:center;margin:40px 0"><a href="#" style="background:linear-gradient(135deg,#667eea 0%,#764ba2 100%);color:#fff;text-decoration:none;padding:16px 32px;border-radius:8px;font-size:16px;font-weight:600;display:inline-block;box-shadow:0 4px 12px rgba(102,126,234,0.3)">Claim Your Discount</a></div><div style="text-align:center;padding:24px;background:#f9fafb;border-radius:8px"><p style="color:#6b7280;font-size:14px;margin:0 0 8px 0">⏰ Offer expires in 7 days</p><p style="color:#9ca3af;font-size:12px;margin:0">Don''t miss out on this exclusive opportunity</p></div></div></div></body></html>'),