INSERT INTO email_templates (name, category, html_content) VALUES

('Survey Request', 'Engagement', '<!DOCTYPE html><html><head><meta charset="utf-8"><meta name="viewport" content="width=device-width,initial-scale=1"></head><body style="margin:0;padding:0;background:#f0f9ff;font-family:Arial,sans-serif"><div style="max-width:600px;margin:0 auto;background:#fff;border-radius:16px;overflow:hidden;box-shadow:0 4px 6px rgba(0,0,0,0.05)"><div style="background:linear-gradient(135deg,#0ea5e9 0%,#0284c7 100%);padding:40px;text-align:center"><div style="width:70px;height:70px;background:rgba(255,255,255,0.2);border-radius:50%;margin:0 auto 20px;display:flex;align-items:center;justify-content:center"><span style="color:#fff;font-size:28px">📊</span></div><h1 style="color:#fff;font-size:26px;margin:0 0 12px 0;font-weight:bold">We Value Your Opinion</h1><p style="color:#bae6fd;font-size:16px;margin:0">Help us serve you better</p></div><div style="padding:40px"><h2 style="color:#1f2937;font-size:22px;margin:0 0 20px 0">Hi {{name}},</h2><p style="color:#4b5563;font-size:16px;line-height:1.6;margin:0 0 24px 0">Your feedback is incredibly valuable to us. We''re constantly working to improve our services, and your insights help us understand what''s working and what we can do better.</p><div style="background:#f0f9ff;border:1px solid #bae6fd;border-radius:12px;padding:24px;margin:24px 0;text-align:center"><h3 style="color:#0c4a6e;font-size:18px;margin:0 0 16px 0">📝 Quick 2-Minute Survey</h3><p style="color:#0369a1;font-size:14px;margin:0 0 20px 0">Your responses are anonymous and will help shape our future updates</p><div style="display:grid;grid-template-columns:1fr 1fr;gap:16px;margin:20px 0"><div style="text-align:center"><div style="color:#0ea5e9;font-size:24px;font-weight:bold">2</div><div style="color:#0369a1;font-size:12px">MINUTES</div></div><div style="text-align:center"><div style="color:#0ea5e9;font-size:24px;font-weight:bold">5</div><div style="color:#0369a1;font-size:12px">QUESTIONS</div></div></div></div><div style="background:#fff;border:2px solid #e0f2fe;border-radius:12px;padding:24px;margin:24px 0"><h4 style="color:#1f2937;font-size:16px;margin:0 0 16px 0">🎁 As a thank you, you''ll receive:</h4><ul style="color:#4b5563;font-size:14px;line-height:1.8;margin:0;padding-left:20px"><li style="margin-bottom:8px">Exclusive access to our upcoming features</li><li style="margin-bottom:8px">Free premium template pack</li><li>Entry into our monthly $500 gift card draw</li></ul></div><div style="text-align:center;margin:32px 0"><a href="#" style="background:linear-gradient(135deg,#0ea5e9 0%,#0284c7 100%);color:#fff;text-decoration:none;padding:16px 32px;border-radius:8px;font-size:16px;font-weight:bold;display:inline-block;box-shadow:0 4px 12px rgba(14,165,233,0.3)">Take Survey Now</a></div><div style="text-align:center;background:#fef3c7;border-radius:8px;padding:16px"><p style="color:#92400e;font-size:13px;margin:0">⏰ Survey closes in 7 days - Don''t miss out!</p></div></div></div></body></html>'),

('Tutorial Series', 'Educational', '<!DOCTYPE html><html><head><meta charset="utf-8"><meta name="viewport" content="width=device-width,initial-scale=1"></head><body style="margin:0;padding:0;background:#fafbfc;font-family:Arial,sans-serif"><div style="max-width:600px;margin:0 auto;background:#fff"><div style="background:linear-gradient(135deg,#10b981 0%,#059669 100%);padding:40px;text-align:center"><div style="width:80px;height:80px;background:rgba(255,255,255,0.2);border-radius:50%;margin:0 auto 20px;display:flex;align-items:center;justify-content:center"><span style="color:#fff;font-size:32px">🎓</span></div><h1 style="color:#fff;font-size:28px;margin:0 0 12px 0;font-weight:bold">Master Class Series</h1><p style="color:#d1fae5;font-size:16px;margin:0">Learn from the experts</p></div><div style="padding:40px"><h2 style="color:#1f2937;font-size:22px;margin:0 0 20px 0">Hello {{name}},</h2><p style="color:#4b5563;font-size:16px;line-height:1.6;margin:0 0 24px 0">Welcome to our comprehensive tutorial series! Over the next few weeks, we''ll guide you through advanced strategies and techniques that will transform your approach.</p><div style="background:#f0fdf4;border-left:4px solid #10b981;padding:24px;margin:24px 0"><h3 style="color:#065f46;font-size:18px;margin:0 0 16px 0">📚 What You''ll Learn</h3><div style="space-y:12px"><div style="display:flex;align-items:start;margin-bottom:12px"><div style="width:24px;height:24px;background:#10b981;border-radius:50%;display:flex;align-items:center;justify-content:center;margin-right:12px;flex-shrink:0;margin-top:2px"><span style="color:#fff;font-size:12px;font-weight:bold">1</span></div><div><h4 style="color:#065f46;font-size:15px;margin:0 0 4px 0;font-weight:600">Foundation Principles</h4><p style="color:#047857;font-size:13px;margin:0;line-height:1.5">Master the core concepts that everything else builds upon</p></div></div><div style="display:flex;align-items:start;margin-bottom:12px"><div style="width:24px;height:24px;background:#10b981;border-radius:50%;display:flex;align-items:center;justify-content:center;margin-right:12px;flex-shrink:0;margin-top:2px"><span style="color:#fff;font-size:12px;font-weight:bold">2</span></div><div><h4 style="color:#065f46;font-size:15px;margin:0 0 4px 0;font-weight:600">Advanced Techniques</h4><p style="color:#047857;font-size:13px;margin:0;line-height:1.5">Discover pro-level strategies used by industry leaders</p></div></div><div style="display:flex;align-items:start"><div style="width:24px;height:24px;background:#10b981;border-radius:50%;display:flex;align-items:center;justify-content:center;margin-right:12px;flex-shrink:0;margin-top:2px"><span style="color:#fff;font-size:12px;font-weight:bold">3</span></div><div><h4 style="color:#065f46;font-size:15px;margin:0 0 4px 0;font-weight:600">Real-World Application</h4><p style="color:#047857;font-size:13px;margin:0;line-height:1.5">Put your knowledge into practice with hands-on projects</p></div></div></div></div><div style="border:1px solid #e5e7eb;border-radius:12px;padding:24px;margin:24px 0"><h3 style="color:#1f2937;font-size:18px;margin:0 0 16px 0">🗓️ Schedule</h3><div style="display:grid;gap:12px"><div style="display:flex;justify-content:space-between;align-items:center;padding:12px;background:#f9fafb;border-radius:6px"><span style="color:#1f2937;font-size:14px;font-weight:600">Week 1: Getting Started</span><span style="color:#6b7280;font-size:12px">Available Now</span></div><div style="display:flex;justify-content:space-between;align-items:center;padding:12px;background:#f9fafb;border-radius:6px"><span style="color:#1f2937;font-size:14px;font-weight:600">Week 2: Intermediate Skills</span><span style="color:#6b7280;font-size:12px">March 15</span></div><div style="display:flex;justify-content:space-between;align-items:center;padding:12px;background:#f9fafb;border-radius:6px"><span style="color:#1f2937;font-size:14px;font-weight:600">Week 3: Advanced Mastery</span><span style="color:#6b7280;font-size:12px">March 22</span></div></div></div><div style="text-align:center;margin:32px 0"><a href="#" style="background:linear-gradient(135deg,#10b981 0%,#059669 100%);color:#fff;text-decoration:none;padding:16px 32px;border-radius:8px;font-size:16px;font-weight:bold;display:inline-block;box-shadow:0 4px 12px rgba(16,185,129,0.3)">Start Learning</a></div></div></div></body></html>'),